import React, { useEffect, useState } from "react";
import { GET_ALL } from "../api/apiService";
import startsActive from "../../assets/images/icons/pay-american-ex.png";
import startsDisable from "../../assets/images/icons/pay-bank.png";
import { Link } from "react-router-dom";
const cardTextStyle = {
  maxWidth: "80%",
};
const LoadProduct = (category) => {
  const { categoryName, categoryId } = category;
  const [products, setProducts] = useState([]);
  useEffect(() => {
    GET_ALL(`products/getlatest?categoryid=${categoryId}`).then((item) =>
      setProducts(item.data)
    );
  }, [categoryId]);
  return (
    <section class="padding-bottom">
      <header class="section-heading mb-4">
        <h3 class="title-section">{categoryName}</h3>
      </header>
      <div class="row">
        {products.length > 0 &&
          products.map((row) => (
            <div class="col-xl-3 col-lg-3 col-md-4 col-6" key={row.id}>
              <div class="card card-product-grid">
                <Link
                  to={`/detailProduct?productId=${row.id}`}
                  class="img-wrap"
                >
                  <img src={`./images/items/${row.thumbnail}`} />
                </Link>
                <figcaption class="info-wrap">
                  <ul class="rating-stars mb-1">
                    <li style={{ cardTextStyle }} class="stars-active">
                      <img src={startsActive} alt="" />
                    </li>
                    <li>
                      <img src={startsDisable} alt="" />
                    </li>
                  </ul>
                  <div>
                    <Link
                      to={`/detailProduct?productId=${row.id}`}
                      class="title"
                    >
                      {row.title}
                    </Link>
                  </div>
                  <div class="price h5 mt-2">${row.price}</div>
                </figcaption>
              </div>
            </div>
          ))}
      </div>

      <div class="col-inner">

<div class="badge-container absolute left top z-1">
		<div class="callout badge badge-circle"><div class="badge-inner secondary on-sale"><span class="onsale">Giảm giá!</span></div></div>
</div>
	<div class="product-small box ">
		<div class="box-image">
			<div class="image-fade_in_back">
				<a href="https://shop.mixigaming.com/san-pham/ao-3-lo-mixi-bl01/">
					<img width="300" height="400" src="https://shop.mixigaming.com/wp-content/uploads/2021/01/Ao-ba-lo-Mixi-QUAY-LEN-300x400.jpg" class="attachment-woocommerce_thumbnail size-woocommerce_thumbnail" alt="Áo Ba Lỗ Mixi QuẨy LÊn" decoding="async" loading="lazy"/>				</a>
			</div>
			<div class="image-tools is-small top right show-on-hover">
							</div>
			<div class="image-tools is-small hide-for-small bottom left show-on-hover">
							</div>
			<div class="image-tools grid-tools text-center hide-for-small bottom hover-slide-in show-on-hover">
							</div>
					</div>


	</div>
		</div>
    </section>

  );
};
export default LoadProduct;
