import React,{useEffect, useState} from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { GET_PAGE } from "../api/apiService";
import { event } from "jquery";

const FilterTop = () =>{

	const [products, setProducts] = useState([]);
	const [totalPages, setTotalPages] = useState(1);
	const numItems = 12;
	const navigate = useNavigate();
	const location = useLocation();
	const queryParams = new URLSearchParams(location.search);
	const currentPage=parseInt(queryParams.get("page")) ||1;


	const handlePageChange = (page)=>{
		navigate(`/listing?page=${page}`);

	};
	const handlePrevious = ()=>{
		if(currentPage > 1){
			handlePageChange(currentPage - 1 );
		}
	};
	const handleNext = ()=>{
		if(currentPage < totalPages){
			handlePageChange(currentPage + 1 );
		}
	};
	const renderPageNumbers = ()=>{
		const pageNumbers = [];
		for (let i=1;i<=totalPages;i++)
		{
			pageNumbers.push(
				<li key={i}
				className={`page-item ${currentPage === i ? "active":""}`}>
					<a
					className="page-link"
					href={`?page=${i}`}
					onClick={(event)=> handlePageChange(i,event)}>
						{i}
					</a>
				</li>
			);
		}
		return pageNumbers;
	};
	useEffect(()=>{
		GET_PAGE(`products`,currentPage, numItems).then((response)=>{
			setProducts(response.data);
			const contentRangeHeader =response.headers["content-range"];
			const [,totalItems]=contentRangeHeader.match(/\/(\d+)/);
			const calculatedTotalpages = Math.ceil(totalItems / numItems);
			setTotalPages(calculatedTotalpages);
		});
	},[currentPage]);
