import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import { useHistory } from 'react-router-dom';

const Login =()=>{

  const [password, setPassword] = useState("");
  const [fullname, setfullname] = useState("");

  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const navigate = useNavigate();
  const [error, setError] = useState('');
  const history = useHistory();


  const handleLogin = () => {
	if (password != "" && fullname != "" )
	{
		axios
		.post(`http://localhost:8080/api/users/login`, {

		  password,
		  fullname,

		})
    .then((response) => {
  if (response.data.success) {
    setIsLoggedIn(true);
    history.push('/'); // Chuyển hướng đến trang chính sau khi đăng nhập
  } else {
    alert("Đăng nhập không thành công");
  }
})
		.catch((error) => {
      console.error(error);
      setError('Tên đăng nhập hoặc mật khẩu không chính xác. Hãy thử lại.');
    });


	}

  };
  useEffect(() => {
    if (isLoggedIn) {

    }
  }, [isLoggedIn]);

		return(
			<section class="section-content padding-y">
		<div class="card mx-auto" style={{ width: '520px' }} >
			<article class="card-body">
				<header class="mb-4"><h4 class="card-title">Sign up</h4></header>
				<form>
					<div class="form-row">

						<label>First name</label>
						<input required="name"
						class="form-control"
							type="fullname"
							placeholder="Fullname"
							value={fullname}
							onChange={(e) => setfullname(e.target.value)}
						/>


					</div>

					<div class="form-row">
						<label>Password</label>
						<input required="password"
						class="form-control"
							type="password"
							placeholder="Mật khẩu"
							value={password}
							onChange={(e) => setPassword(e.target.value)}
						/>
					</div>



					:

					<div class="form-group">
					{error && <p className="text-danger">{error}</p>}
<button    onClick={handleLogin} type="submit" class="btn btn-primary btn-block"> Login  </button>
					</div>

				</form>
			</article>
		</div>


		</section>

		);


};
export default Login
