import React, { Component, useEffect, useState } from "react";
import logo from "../assets/images/logo06.png";
import { GET_ALL } from "../pages/api/apiService";
import Search from "../pages/search/Search";
function Header() {
  const fullname = localStorage.getItem("fullname");
  const handleLogout = () => {
    // Xóa thông tin người dùng khỏi local storage khi đăng xuất
    localStorage.removeItem("fullname");
    window.location.reload();
    // Thực hiện các bước đăng xuất khác (nếu có)
  };
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Lấy thông tin giỏ hàng từ localStorage hoặc API
    const storedCartItems = localStorage.getItem("cartItems") || "[]";
    const parsedCartItems = JSON.parse(storedCartItems);
    setCartItems(parsedCartItems);
  }, []);
  const [categories, setCategories] = useState([]);
  useEffect(() => {
    GET_ALL(`categories`).then((item) => setCategories(item.data));
  }, []);
  return (
    <header class="section-header">


      <nav class="navbar navbar-main navbar-expand-lg border-bottom">
        <div class="container">
          <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#main_nav"
            aria-controls="main_nav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon"></span>
          </button>

          <div class="collapse navbar-collapse" id="main_nav">
            <ul class="navbar-nav">
              <li class="nav-item dropdown">
                <a
                  class="nav-link dropdown-toggle"
                  data-toggle="dropdown"
                  href="#"
                >
                  {" "}
                  <i class="fa fa-bars text-muted mr-2"></i> Danh mục sản phẩm{" "}
                </a>
                <div class="dropdown-menu dropdown-large">
                  <nav class="row">
                    <div class="col-6">
                      {categories.length > 0 &&
                        categories.map((row) => (
                          <a
                            class="dropdown-item"
                            href={`/listing?categoryId=${row.id}`}
                          >
                            {row.name}
                          </a>
                        ))}
                      <a class="dropdown-item" href="/listing">
                        Tất cả sản phẩm
                      </a>

                    </div>
                  </nav>
                </div>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="/information">
                  Thông Báo
                </a>
              </li>
            </ul>
            <ul class="navbar-nav ml-md-auto">
              <li class="nav-item dropdown">
                <a
                  class="nav-link dropdown-toggle"
                  href="http://example.com"
                  data-toggle="dropdown"
                >
                  English
                </a>
                <div class="dropdown-menu dropdown-menu-right">
                  <a class="dropdown-item" href="#">
                    Russian
                  </a>
                  <a class="dropdown-item" href="#">
                    French
                  </a>
                  <a class="dropdown-item" href="#">
                    Spanish
                  </a>
                  <a class="dropdown-item" href="#">
                    Chinese
                  </a>
                </div>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
}

export default Header;
