import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Content = (setCartItems ) => {
    const [products, setProducts] = useState([]);
   const navigative=useNavigate()
    // Retrieve data from localStorage when the component mounts
    useEffect(() => {
        const storedCartItems = localStorage.getItem("cartItems") || "[]";
        const parsedCartItems = JSON.parse(storedCartItems);
        setProducts(parsedCartItems);

    }, []); // The empty dependency array ensures this effect runs only once on mount
    const handleRemoveCartItem = (productId) => {
        // Filter out the item with the given productId
        const updatedCartItems = products.filter((item) => item.id !== productId);


        // Update state and localStorage with the new cart items
        setProducts(updatedCartItems);
    localStorage.setItem("cartItems", JSON.stringify(updatedCartItems));

    // Cập nhật giỏ hàng ở component cha
    setCartItems(updatedCartItems);
  };
    const handleUpdateQuantity = (productId, newQuantity) => {
        const updatedCartItems = products.map((item) =>
            item.id === productId ? { ...item, quantity: newQuantity } : item
        );

        setProducts(updatedCartItems);
        localStorage.setItem("cartItems", JSON.stringify(updatedCartItems));
        window.location.reload();
    };

    // Function to increase the quantity
    const handleIncreaseQuantity = (productId, currentQuantity) => {
        handleUpdateQuantity(productId, currentQuantity + 1);
    };

    // Function to decrease the quantity
    const handleDecreaseQuantity = (productId, currentQuantity) => {
        if (currentQuantity > 1) {
            handleUpdateQuantity(productId, currentQuantity - 1);
        }
    };

    // Calculate the total price
    const totalPrice = products.reduce(
        (total, product) => total + product.price * product.quantity,
        0
    );
    const handleCheckout = () => {
        // Thực hiện xử lý thanh toán ở đây (ví dụ: gửi đơn hàng đến server)
        // Sau khi thanh toán thành công, cập nhật giỏ hàng và điều hướng người dùng đến trang thanh toán
        setCartItems([]);
        localStorage.removeItem("cartItems");
        navigative("/formcart"); // Điều hướng đến trang thanh toán
      };
      const handleContinueShopping = () => {
        // Điều hướng người dùng về trang chủ hoặc trang sản phẩm khi nhấp vào "Continue shopping"
        navigative("/");
      };
    return (
        <section ClassName="section-content padding-y">
            <div ClassName="container">

                <div ClassName="row">
                    <main ClassName="col-md-9">
                        <div ClassName="card">
                            {products.length > 0 &&
                                products.map((row) => (<table ClassName="table table-borderless table-shopping-cart">
                                    <thead ClassName="text-muted">
                                        <tr ClassName="small text-uppercase">
                                            <th scope="col">Product</th>
                                            <th scope="col" width="120">Quantity</th>
                                            <th scope="col" width="120">Price</th>
                                            <th scope="col" ClassName="text-right" width="200"> </th>
                                        </tr>
                                    </thead>
                                    <tbody>
<tr>
                                            <td>
                                                <figure ClassName="itemside">
                                                    <div ClassName="aside">
                                                    <img src={`../images/items/${row.thumbnail}`} style={{width:"100px"}} />
                                                        </div>
                                                    <figcaption ClassName="info">
                                                        <a href="#" ClassName="title text-dark">{row.title}</a>

                                                    </figcaption>
                                                </figure>
                                            </td>
                                            <td>
                                                <div ClassName="quantity-control">
                                                    <button
                                                        onClick={() => handleDecreaseQuantity(row.id, row.quantity)}
                                                        ClassName="btn btn-outline-secondary btn-sm"
                                                    >
                                                        -
                                                    </button>
                                                    <span ClassName="quantity">{row.quantity}</span>
                                                    <button
                                                        onClick={() => handleIncreaseQuantity(row.id, row.quantity)}
                                                        ClassName="btn btn-outline-secondary btn-sm"
                                                    >
                                                        +
                                                    </button>
                                                </div>
                                            </td>

                                            <td>
                                                <div ClassName="price-wrap">
                                                    <var ClassName="price">${row.price * row.quantity}</var>
                                                    <small ClassName="text-muted"> ${row.price} each </small>
                                                </div>
                                            </td>
                                            <td ClassName="text-right">
                                                <a data-original-title="Save to Wishlist" title="" href="" ClassName="btn btn-info" data-toggle="tooltip"> <i ClassName="fa fa-heart"></i></a>
                                                <a href="" ClassName="btn btn-light" onClick={() => handleRemoveCartItem(row.id)}> Remove</a>
                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
))}
                           <div ClassNameName="card-body border-top">
                <Link to="/formcart" ClassNameName="btn btn-info float-md-right" onClick={handleCheckout}>
                  Mua hàng <i ClassNameName="fa fa-chevron-right"></i>
                </Link>
                <button ClassNameName="btn btn-light" onClick={handleContinueShopping}>
                  <i ClassNameName="fa fa-chevron-left"></i> Continue shopping
                </button>
              </div>
                        </div>

                        <div ClassName="alert alert-success mt-3">
                            <p ClassName="icontext"><i ClassName="icon text-success fa fa-truck"></i> Free Delivery within 1-2 weeks</p>
                        </div>

                    </main>
                    <aside ClassName="col-md-3">
                        <div ClassName="card mb-3">
                            <div ClassName="card-body">
                                <form>
                                    <div ClassName="form-group">
                                        <label>Have coupon?</label>
                                        <div ClassName="input-group">
                                            <input type="text" ClassName="form-control" name="" placeholder="Coupon code" />
                                            <span ClassName="input-group-append">
                                                <button ClassName="btn btn-info">Apply</button>
                                            </span>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div ClassName="card">
                            <div ClassName="card-body">
                                <dl ClassName="dlist-align">
                                    <dt>Total price:</dt>
                                    <dd ClassName="text-right">${totalPrice}</dd>
                                </dl>
                                <dl ClassName="dlist-align">
                                    <dt>Discount:</dt>
                                    <dd ClassName="text-right">$0</dd>
                                </dl>


                                <p ClassName="text-center mb-3">
                                    <img src={require("../../assets/images/misc/payments.png")} style={{ height: "26" }} />
                                </p>

                            </div>
                        </div>
                    </aside>
                </div>

            </div>
        </section>)

};

export default Content
