import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
    const [fullname, setFullname] = useState("");
    const [password, setPassword] = useState("");
	const navigate = useNavigate();

    const handleLogin = () => {
        axios.post("http://localhost:8080/api/users/login", {
            fullname: fullname,
            password: password,
        })
        .then(response => {
            if (response.status === 200) {
                alert("Login successful");
				navigate('/');
            } else {
                alert("Login failed");
            }
        })
        .catch(error => {
            console.error("Login failed:", error);
            alert("Login failed");
        });

    };

    return (
        <section class="section-content padding-y">
		<div class="card mx-auto" style={{ width: '520px' }} >
			<article class="card-body">
				<header class="mb-4"><h4 class="card-title">Sign up</h4></header>
				<form>
					<div class="form-row">

						<label>First name</label>
						<input required="name"
						class="form-control"
							type="fullname"
							placeholder="Fullname"
							value={fullname}
							onChange={(e) => setfullname(e.target.value)}
						/>


					</div>
					<div class="form-group">
						<label>Email</label>
						<input required="email"
						class="form-control"
							type="email"
							placeholder="Email"
							value={email}
							onChange={(e) => setEmail(e.target.value)}
						/>

					</div>

					<div class="form-row">
						<label>Password</label>
						<input required="password"
						class="form-control"
							type="password"
							placeholder="Mật khẩu"
							value={password}
							onChange={(e) => setPassword(e.target.value)}
						/>
					</div>

					<div class="form-row">
						<label>address</label>
						<input required="address"
						class="form-control"
							type="address"
							placeholder="address"
							value={address}
							onChange={(e) => setaddress(e.target.value)}
						/>
					</div>

					<div class="form-row">
						<label>Phone</label>
						<input required="phone_number"
						class="form-control"
							type="phone_number"
							placeholder="phone_number"
							value={phone_number}
							onChange={(e) => setphone_number(e.target.value)}
						/>
					</div>:

					<div class="form-group">
					{error && <p className="text-danger">{error}</p>}
<button    onClick={handleRegister} type="submit" class="btn btn-primary btn-block"> Register  </button>
					</div>

				</form>
			</article>
		</div>


		</section>
    );
};

export default Login;
