import React, { Component, useEffect, useState } from "react";
import logo from "../assets/images/logo06.png";
import { GET_ALL } from "../pages/api/apiService";
import Search from "../pages/search/Search";
import axios from "axios";
function Header() {
  const fullname = localStorage.getItem("fullname");
  const handleLogout = () => {
    // Xóa thông tin người dùng khỏi local storage khi đăng xuất
    localStorage.removeItem("fullname");
    window.location.reload();
    // Thực hiện các bước đăng xuất khác (nếu có)
  };

  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Lấy thông tin giỏ hàng từ localStorage hoặc API
    const storedCartItems = localStorage.getItem("cartItems") || "[]";
    const parsedCartItems = JSON.parse(storedCartItems);
    setCartItems(parsedCartItems);
  }, []);
  const [categories, setCategories] = useState([]);
  useEffect(() => {
    GET_ALL(`categories`).then((item) => {
      const categoriesWithSubcategories = item.data.map((category) => {
        // Assume 'subCategories' is an array of subcategories for each category
        category.subCategories = []; // You can fetch subcategories from API or define them
        return category;
      });
      setCategories(categoriesWithSubcategories);
    });
  }, []);
  const MenuList = () => {
    const [menus, setMenus] = useState([]);

    useEffect(() => {
      axios.get('http://localhost:8080/api/Menus')
        .then(response => {
          setMenus(response.data);
        })
        .catch(error => {
          console.error('Error fetching menus: ', error);
        });
    }, []);
  return (
    <header class="section-header">
      <section class="header-main border-bottom">
        <div class="container">
          <div class="row align-items-center">
            <div id="logo" class="flex-col logo">
              <a href="/" title="MixiShop - MixiShop" rel="home">
                <img
                  width="151"
                  height="100"
                  src="https://shop.mixigaming.com/wp-content/uploads/2019/06/logo-mixi-tét.png"
                  class="header-logo-dark"
                  alt="MixiShop"
                />
              </a>
            </div>
            <Search />
            <div class="col-xl-4 col-lg-4 col-md-6">
              <div class="widgets-wrap float-md-right">
                <div class="widget-header mr-3">
                  <a href="/login" class="widget-view">
                    <div class="icon-area">
                      <i class="fa fa-user"></i>

                    </div>
                    <small class="text">{fullname ? fullname : "Login"}</small>
                  </a>
                </div>



                <div class="widget-header mr-3">
                  <a href="#" class="widget-view">
                    <div class="icon-area">
                      <i class="fa fa-comment-dots"></i>

                    </div>
                    <small class="text"> Message </small>
                  </a>
                </div>
                <div class="widget-header mr-3">
                  <a href="/profile/order " class="widget-view">
                    <div class="icon-area">
                      <i class="fa fa-store"></i>
                    </div>
                    <small class="text"> Orders </small>
                  </a>
                </div>
                <div class="widget-header">
                  <a href="/shopping/cart" class="widget-view">
                    <div class="icon-area">
                      <i class="fa fa-shopping-cart"></i>
                      <span class="notify">{cartItems.length}</span>
                    </div>
                    <small class="text"> Cart </small>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <nav class="navbar navbar-main navbar-expand-lg border-bottom">
        <div class="container">
          <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#main_nav"
            aria-controls="main_nav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon"></span>
          </button>

          <div class="collapse navbar-collapse" id="main_nav">
            <ul class="navbar-nav">
              <li class="nav-item dropdown">
                <a
                  class="nav-link dropdown-toggle"
                  data-toggle="dropdown"
                  href="#"
                >

                  <i class="fa fa-bars text-muted mr-2"></i> Danh mục sản phẩm{" "}
                </a>
                <div class="dropdown-menu dropdown-large">
                  <nav class="row">
                    <div class="col-6">
                      {categories.length > 0 &&
                        categories.map((row) => (
                          <a
                            class="dropdown-item"
                            href={`/listing?categoryId=${row.id}`}
                          >
                            {row.name}
                          </a>
                        ))}
                      <a class="dropdown-item" href="/listing">
                        Tất cả sản phẩm
                      </a>
                      {/* <a href="page-listing-grid.html">Áo PUBG</a>
                                <a href="page-shopping-cart.html">Áo CSGO</a>
                                <a href="page-detail-product.html">Áo LOL</a> */}
                    </div>
                  </nav>
                </div>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="/information">
                <ul>
        {categories.map(category => (
          <li key={category.id}>
            {category.name}
            {category.subCategories && category.subCategories.length > 0 && (
              <ul>
                {category.subCategories.map(subCategory => (
                  <li key={subCategory.id}>{subCategory.name}</li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
                </a>
              </li>
            </ul>
            <ul class="navbar-nav ml-md-auto">
              <li class="nav-item dropdown">
                <a
                  class="nav-link dropdown-toggle"
                  href="http://example.com"
                  data-toggle="dropdown"
                >
                  English
                </a>
                <div class="dropdown-menu dropdown-menu-right">
                  <a class="dropdown-item" href="#">
                    Russian
                  </a>
                  <a class="dropdown-item" href="#">
                    French
                  </a>
                  <a class="dropdown-item" href="#">
                    Spanish
                  </a>
                  <a class="dropdown-item" href="#">
                    Chinese
                  </a>
                </div>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
}}

export default Header;
