import React,{useEffect, useState} from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { GET_PAGE } from "../api/apiService";
import { event } from "jquery";

const FilterTop = () =>{

	const [products, setProducts] = useState([]);
	const [totalPages, setTotalPages] = useState(1);
	const numItems = 12;
	const navigate = useNavigate();
	const location = useLocation();
	const queryParams = new URLSearchParams(location.search);
	const currentPage=parseInt(queryParams.get("page")) ||1;


	const handlePageChange = (page)=>{
		navigate(`/listing?page=${page}`);

	};
	const handlePrevious = ()=>{
		if(currentPage > 1){
			handlePageChange(currentPage - 1 );
		}
	};
	const handleNext = ()=>{
		if(currentPage < totalPages){
			handlePageChange(currentPage + 1 );
		}
	};
	const renderPageNumbers = ()=>{
		const pageNumbers = [];
		for (let i=1;i<=totalPages;i++)
		{
			pageNumbers.push(
				<li key={i}
				className={`page-item ${currentPage === i ? "active":""}`}>
					<a
					className="page-link"
					href={`?page=${i}`}
					onClick={(event)=> handlePageChange(i,event)}>
						{i}
					</a>
				</li>
			);
		}
		return pageNumbers;
	};
	useEffect(()=>{
		GET_PAGE(`products`,currentPage, numItems).then((response)=>{
			setProducts(response.data);
			const contentRangeHeader =response.headers["content-range"];
			const [,totalItems]=contentRangeHeader.match(/\/(\d+)/);
			const calculatedTotalpages = Math.ceil(totalItems / numItems);
			setTotalPages(calculatedTotalpages);
		});
	},[currentPage]);
	return(
		 <section class="section-content padding-y">
		<div class="container">




		<header class="mb-3">
				<div class="form-inline">
					<strong class="mr-md-auto">21 </strong>
					<select class="mr-2 form-control">
						<option>Latest items</option>
						<option>Trending</option>
						<option>Most Popular</option>
						<option>Cheapest</option>
					</select>
					<div class="btn-group">
						<a href="page-listing-grid.html" class="btn btn-light active" data-toggle="tooltip" title="List view"> 
							<i class="fa fa-bars"></i></a>
						<a href="page-listing-large.html" class="btn btn-light" data-toggle="tooltip" title="Grid view"> 
							<i class="fa fa-th"></i></a>
					</div>
				</div>
		</header>

		<div class="row">
			{products.length>0&&
			products.map((row)=>(


			<div class="col-md-3">
				<figure class="card card-product-grid">
					<div class="img-wrap"> 
						<span class="badge badge-danger"> NEW </span>
						<a href="#" class="item-thumb"> <img src={`./images/items/${row.thumbnail}`} /></a>

					</div> 
					<figcaption class="info-wrap">
							<a href="#" class="title mb-2">{row.title}</a>
							<div class="price-wrap">
								<span class="price">{row.price}</span> 
								<small class="text-muted">/per item</small>
							</div> 





							<a href="#" class="btn btn-outline-primary"> <i class="fa fa-envelope"></i> Contact supplier </a>	

					</figcaption>
				</figure>
			</div> 

		))}
		</div> 


		<nav class="mb-4" aria-label="Page navigation sample">
		  <ul class="pagination">
			<li class={`page-item active ${currentPage ===1 ? "disable":""}`}>
				<a class="page-link" href="#" onclick={handlePrevious}>Previous</a></li>{renderPageNumbers()}


			<li className={`page-item${currentPage === totalPages ? "disable":""}`} >
				<a class="page-link" href="#"onclick={handleNext}>Next</a></li>
		  </ul>
		</nav>




		</div> 
		</section>
		)






};
export default FilterTop
