import React, { Component, useEffect, useState } from "react";
import logo from "../assets/images/logo06.png";
import { GET_ALL } from "../pages/api/apiService";
import Search from "../pages/search/Search";
import { Link } from "react-router-dom";
function Header() {
  const fullname = localStorage.getItem("fullname");
  const handleLogout = () => {
    // Xóa thông tin người dùng khỏi local storage khi đăng xuất
    localStorage.removeItem("fullname");
    window.location.reload();
    // Thực hiện các bước đăng xuất khác (nếu có)
  };
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Lấy thông tin giỏ hàng từ localStorage hoặc API
    const storedCartItems = localStorage.getItem("cartItems") || "[]";
    const parsedCartItems = JSON.parse(storedCartItems);
    setCartItems(parsedCartItems);
  }, []);
  const [categories, setCategories] = useState([]);
  useEffect(() => {
    GET_ALL(`categories`).then((item) => setCategories(item.data));
  }, []);
  return (
    <header class="section-header">


      <nav className="navbar navbar-main navbar-expand pl-0">
        <ul className="navbar-nav flex-wrap">
          <li className="nav-item">
            <Link className="nav-link" to="/Home">
              Trang chủ
            </Link>
          </li>
          <li class="nav-item dropdown">
            <a
              class="nav-link dropdown-toggle"
              href="#"
              id="navbarDropdown"
              role="button"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              Danh sách sản phẩm
            </a>
            <div class="dropdown-menu" aria-labelledby="navbarDropdown">
              {categories.length > 0 &&
                categories.map((row) => (
                  <a
                    class="dropdown-item"
                    href={`/ListingGrid?categoryId=${row.id}`}
                  >
                    {row.name}
                  </a>
                ))}

              <div class="dropdown-divider"></div>
              <a class="dropdown-item" href="/ListingGrid">
                Tất cả sản phẩm
              </a>
            </div>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Điện tử
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Thời trang
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Làm đẹp
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Xe hơi
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Thể thao
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Nông trại và vườn
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Khuyến mãi
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Dưới $10
            </a>
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
