import React, { useEffect, useState } from "react";
import { GET_ALL } from "../api/apiService.js";
import sliderImage1 from "../../assets/images/banners/mixishop2.jpg";
import sliderImage2 from "../../assets/images/banners/mixishop4.jpg";

const Slider = () => {
  const [sliders, setSliders] = useState([]);

  useEffect(() => {
    // Fetch slider images from the API
    GET_ALL(`Banners`).then((response) => {
      setSliders(response.data);
    });
  }, []);

  // Kiểm tra đường dẫn ảnh
  useEffect(() => {
    console.log("./images/banner/", sliders.map((slider) => slider.imageUrl));
  }, [sliders]);


  return (
    <section className="section-main padding-y">
      <main className="card">
        <div className="card-body">
          <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
            <ol className="carousel-indicators">
              {sliders.map((slider, index) => (
                <li
                  key={index}
                  data-target="#carouselExampleIndicators"
                  data-slide-to={index}
                  className={index === 0 ? "active" : ""}
                ></li>
              ))}
            </ol>
            <div className="carousel-inner">
            {sliders.map((slider, index) => (
  <div key={index} className={`carousel-item ${index === 0 ? "active" : ""}`}>

    <img src={`./images/banner/${slider.banners}`} style={{ width: "100%" }} alt={`Slide ${index + 1}`} />
  </div>
))}
            </div>
            <a className="carousel-control-prev" href="#carouselExampleIndicators" role="button" data-slide="prev">
              <span className="carousel-control-prev-icon" aria-hidden="true"></span>
              <span className="sr-only">Previous</span>
            </a>
            <a className="carousel-control-next" href="#carouselExampleIndicators" role="button" data-slide="next">
              <span className="carousel-control-next-icon" aria-hidden="true"></span>
              <span className="sr-only">Next</span>
            </a>
          </div>
        </div>
      </main>
    </section>
  );
};

export default Slider;
