import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
    const SearchResults = ({ results }) => (
        <table className="search-results-table ms-3">
          <tbody>
            {results.slice(0, 5).map((result) => {
              console.log('Result:', result); // Log để kiểm tra giá trị của result
              return (
                <tr key={result.id} className="search-result-row">
                  <td className="image-cell">
                    <Link to={`/product-detail?productId=${result.id}`}>
                      <img src={`./images/items/${result.thumbnail}`} alt={`Thumbnail for ${result.title}`} style={{ height: '100px' }}/>
                    </Link>
                  </td>
                  <td className="info-cell mt-4">
                    <div className='title'>{result.title}</div>
                    <div className='price'>{result.price}.000.000đ</div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      );

    return (
        <section class="section-content padding-y">
		<div class="card mx-auto" style={{ width: '520px' }} >
			<article class="card-body">
				<header class="mb-4"><h4 class="card-title">Sign up</h4></header>
                <form class="form-row">
                <div>
            <label>Fullname:</label>
            <input required="name"
						class="form-control"
							type="fullname"
							placeholder="Fullname"
							value={fullname}
							onChange={(e) => setFullname(e.target.value)}
						/>

            <label>Password:</label>
            <input required="password"
						class="form-control"
							type="password"
							placeholder="Mật khẩu"
							value={password}
							onChange={(e) => setPassword(e.target.value)}
						/>

<button    onClick={handleLogin} type="submit" class="btn btn-primary btn-block"> Login  </button>
<a

                  href="/register"

                >
                 Register
                </a>
        </div>

                </form>

			</article>
		</div>


		</section>

    );
};

export default Login;
