import React, { useEffect, useState } from "react";
import { GET_ALL } from "../api/apiService";
import startsActive from "../../assets/images/icons/pay-american-ex.png";
import startsDisable from "../../assets/images/icons/pay-bank.png";
import { Link } from "react-router-dom";
const cardTextStyle = {
	maxWidth: "80%",
	};
	const Items = (category) => {
		const { categoryName, categoryId } = category;
		const [products, setProducts] = useState([]);
		useEffect(() => {
		GET_ALL(`products/getlatest?categoryid=${categoryId}`).then((item) =>
		setProducts(item.data)
		);
		}, [categoryId]);
		return (
<section  class="padding-bottom-sm">

<header class="section-heading heading-line">
	<h4 class="title-section text-uppercase">Recommended items</h4>
</header>

<div class="row">
		{products.length > 0 &&
		products.map((row) => (
		<div class="col-xl-3 col-lg-3 col-md-4 col-6" key={row.id}>
		<div class="card card-product-grid">
		<Link to={`/detailProduct?productId=${row.id}`} class="img-wrap">
		<img src={`./images/items/${row.thumbnail}`} />{" "}
		</Link>
		<figcaption class="info-wrap">
		<ul class="rating-stars mb-1">
		<li style={{ cardTextStyle }} class="stars-active">
		<img src={startsActive} alt="" />
		</li>
		<li>
		<img src={startsDisable} alt="" />
		</li>
		</ul>
		<div>
		<Link to={`/detailProduct?productId=${row.id}`} class="title">
		{row.title}
		</Link>
		</div>
		<div class="price h5 mt-2">${row.price}</div>
		</figcaption>
		</div>
		</div>
		))}
		</div>

<div class="row row-sm">
	<div class="col-xl-2 col-lg-3 col-md-4 col-6">
		<div class="card card-sm card-product-grid">
			<a href="#" class="img-wrap"> <img src={require("../../assets/images/items/aorefund.jpg")}style={{width:"70%"}}/> </a>
			<figcaption class="info-wrap">
				<a href="#" class="title">AÃÅo Refung gaming</a>
				<div class="price mt-1">$10</div> 
			</figcaption>
		</div>
	</div> 

</div> 
</section>
);
		};
export default Items
