import React, { useEffect, useState } from "react";
import { GET_ALL } from "../pages/api/apiService";
import Search from "../pages/search/Search";
import axios from "axios";
import MenuList from "../pages/menu/Menulist";
function Header() {
  const fullname = localStorage.getItem("fullname");
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Lấy thông tin giỏ hàng từ localStorage hoặc API
    const storedCartItems = localStorage.getItem("cartItems") || "[]";
    const parsedCartItems = JSON.parse(storedCartItems);
    setCartItems(parsedCartItems);
  }, []);
  const [categories, setCategories] = useState([]);
  useEffect(() => {
    GET_ALL(`categories`).then((item) => setCategories(item.data));
  }, []);
  const [setMenus] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8080/api/Menus')
            .then(response => {
                setMenus(response.data);
            })
            .catch(error => {
                console.error('Error fetching menus: ', error);
            });
    }, []);
        useEffect(() => {
        // Kiểm tra xem token đã tồn tại trong localStorage hay không
        const token = localStorage.getItem("token");

        // Nếu có token, có thể thực hiện các xử lý khác nhau tại đây (ví dụ: chuyển hướng người dùng)
    }, []);
// Trong một component khác (ví dụ: Header, Navigation, hoặc Component chứa nút đăng xuất)
const LogoutButton = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
      // Xóa token khỏi localStorage
      localStorage.removeItem("token");
      localStorage.removeItem("fullname");

      // Thực hiện các xử lý khác nếu cần thiết (ví dụ: chuyển hướng người dùng)
      // ...

      // Hiển thị thông báo đăng xuất thành công (tuỳ chọn)
      alert("Logout successful");

      // Chuyển hướng người dùng (ví dụ: trở về trang đăng nhập)
      navigate("/login");
  };

  return (
      <button onClick={handleLogout}>Logout</button>
  );
};


  return (
    <header class="section-header">
      <section class="header-main border-bottom">
        <div class="container">
          <div class="row align-items-center">
            <div id="logo" class="flex-col logo">
              <a href="/" title="MixiShop - MixiShop" rel="home">
                <img
                  width="151"
                  height="100"
                  src="https://shop.mixigaming.com/wp-content/uploads/2019/06/logo-mixi-tét.png"
                  class="header-logo-dark"
                  alt="MixiShop"
                />
              </a>
            </div>
            <Search />
            <div class="col-xl-4 col-lg-4 col-md-6">
              <div class="widgets-wrap float-md-right">
                <div class="widget-header mr-3">
                  <a href="/login" class="widget-view">
                    <div class="icon-area">
                      <i class="fa fa-user"></i>

                    </div>
                    <small class="text">{fullname ? fullname : "Login"}</small>
                  </a>
                </div>



                <div class="widget-header mr-3">
                  <a href="#" class="widget-view">
                    <div class="icon-area">
                      <i class="fa fa-comment-dots"></i>

                    </div>
                    <small class="text"> Message </small>
                  </a>
                </div>
                <div class="widget-header mr-3">
                  <a href="/profile/order " class="widget-view">
                    <div class="icon-area">
                      <i class="fa fa-store"></i>
                    </div>
                    <small class="text"> Orders </small>
                  </a>
                </div>
                <div class="widget-header">
                  <a href="/shopping/cart" class="widget-view">
                    <div class="icon-area">
                      <i class="fa fa-shopping-cart"></i>
                      <span class="notify">{cartItems.length}</span>
                    </div>
                    <small class="text"> Cart </small>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <nav class="navbar navbar-main navbar-expand-lg border-bottom">
        <div class="container">
          <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#main_nav"
            aria-controls="main_nav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon"></span>
          </button>

          <div class="collapse navbar-collapse" id="main_nav">
            <ul class="navbar-nav">
              <li class="nav-item dropdown">
                <a
                  class="nav-link dropdown-toggle"
                  data-toggle="dropdown"
                  href="#"
                >
                  {" "}
                  <i class="fa fa-bars text-muted mr-2"></i> Danh mục sản phẩm{" "}
                </a>
                <div class="dropdown-menu dropdown-large">
                  <nav class="row">
                    <div class="col-6">
                      {categories.length > 0 &&
                        categories.map((row) => (
                          <a
                            class="dropdown-item"
                            href={`/listing?categoryId=${row.id}`}
                          >
                            {row.name}
                          </a>
                        ))}

                    </div>
                  </nav>
                </div>
              </li>

              <MenuList/>

            </ul>

          </div>
        </div>
      </nav>
    </header>
  );
}

export default Header;
