import React, { useEffect, useState } from "react";
import { GET_ALL } from "../api/apiService";
import startsActive from "../../assets/images/icons/pay-american-ex.png";
import startsDisable from "../../assets/images/icons/pay-bank.png";
import { Link } from "react-router-dom";
const cardTextStyle = {
  maxWidth: "80%",
};
const LoadProduct = (category) => {
  const { categoryName, categoryId } = category;
  const [products, setProducts] = useState([]);
  useEffect(() => {
    GET_ALL(`products/getlatest?categoryid=${categoryId}`).then((item) =>
      setProducts(item.data)
    );
  }, [categoryId]);
  return (
    <section class="padding-bottom">
      <header class="section-heading mb-4">
        <h3 class="title-section">recommend</h3>
		<div class="row">
        {products.length > 0 &&
          products.map((row) => (
            <div class="col-xl-2 col-lg-3 col-md-4 col-6">
		<div class="card card-sm card-product-grid">
			<a href="#" class="img-wrap"> <img src={require("../../assets/images/items/aocsgo4.jpg")}style={{width:"70%"}}/> </a>
			<figcaption class="info-wrap">
				<a href="#" class="title">Áo thun đen CSGO rusb B</a>
				<div class="price mt-1">$10</div> 
			</figcaption>
		</div>
	</div> 
          ))}
      </div>
      </header>

    </section>
  );
};
export default LoadProduct;
