// Ví dụ ReactJS Component (điều chỉnh tùy thuộc vào thư viện frontend bạn đang sử dụng)
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';

const Login =()=>{

	const [username, setUsername] = useState('');
	const [password, setPassword] = useState('');
	const [message, setMessage] = useState('');

	const handleLogin = async () => {
	  try {
		const response = await axios.post('http://localhost:8080/api/login', {
		  username,
		  password,
		});
		setMessage(response.data);
	  } catch (error) {
		setMessage('Login failed. Please check your credentials.');
	  }
	};

    return (
        <section class="section-content padding-y">
		<div class="card mx-auto" style={{ width: '520px' }} >
			<article class="card-body">
				<header class="mb-4"><h4 class="card-title">Sign up</h4></header>
				<form>
					<div class="form-row">

						<label>First name</label>
						<input required="name"
						class="form-control"
							type="fullname"
							placeholder="Fullname"
							value={fullname}
							onChange={(e) => setfullname(e.target.value)}
						/>


					</div>


					<div class="form-row">
						<label>Password</label>
						<input required="password"
						class="form-control"
							type="password"
							placeholder="Mật khẩu"
							value={password}
							onChange={(e) => setPassword(e.target.value)}
						/>
					</div>





					<div class="form-group">
					{error && <p className="text-danger">{error}</p>}
<button    onClick={handleLogin} type="submit" class="btn btn-primary btn-block "> Login  </button>
					</div>
					<div class="form-group">
						<label class="custom-control custom-checkbox"> <input type="checkbox" class="custom-control-input" checked="" /> <div class="custom-control-label"> I am agree with <a href="#">terms and contitions</a>  </div> </label>
					</div>
                    <div class="form-group">
						<label >   <a href="/register">Register</a>   </label>
					</div>
				</form>
			</article>
		</div>


		</section>
    );
};

export default Login;
