import React, { useEffect, useState,useContext } from "react";
import { GET_ID } from "../../api/apiService";
import { useLocation } from "react-router-dom";
import Account from "../order/Account"
import Header from "../../layouts/Header";

const cardTextStyle = {
	maxWidth: "80%",
};
const Detail = () => {

	const [product, setProduct] = useState({});
	const location = useLocation();

	const queryParams = new URLSearchParams(location.search);
	const productId = queryParams.get("productId");
	const [cartItems, setCartItems] = useState([]);
	useEffect(() => {
		GET_ID(`products`, productId).then((item) => setProduct(item.data));

	}, [productId]);

	const handleAddToCart = () => {
		// Check if product is defined and has an id
		if (!product || !product.id) {
		  console.error('Product is not defined or does not have an id.');
		  return;
		}

		const existingItemIndex = cartItems.findIndex((item) => item.id === product.id);

		if (existingItemIndex !== -1) {
		  const updatedCartItems = [...cartItems];
		  updatedCartItems[existingItemIndex].quantity += quantity;
		  setCartItems(updatedCartItems);
		  localStorage.setItem('cartItems', JSON.stringify(updatedCartItems));
		} else {
		  const cartItem = {
			id: product.id,
			title: product.title,
			price: product.price,
			quantity: quantity,
			thumbnail: product.thumbnail,
		  };

		  setCartItems((prevCartItems) => [...prevCartItems, cartItem]);
		  localStorage.setItem('cartItems', JSON.stringify([...cartItems, cartItem]));
		}

		// Reset quantity to 1 after adding to cart
		setQuantity(1);
		window.location.reload();


		// Show success message (you can implement this as per your UI)
		alert('Product added to cart successfully!');
	  };
	const [quantity, setQuantity] = useState(1);
	const handleIncreaseQuantity = () => {
		setQuantity(quantity + 1);
	  };

	  const handleDecreaseQuantity = () => {
		setQuantity(quantity - 1);
	  };



	return (
		<section>
			<section className="py-3 bg-light">
				<div className="container">
					<ol className="breadcrumb">
						<li className="breadcrumb-item">

							<a>Home</a>

						</li>
						<li className="breadcrumb-item">
							<a>{product.category && product.category.name}</a>
						</li>
						<li class="breadcrumb-item active" aria-current="page">{product.title}
						</li>
					</ol>
				</div>
			</section>
			<section class="section-content bg-white padding-y">

				<div class="container">
					<div class="row">
						<aside class="col-md-6">
							<div class="card">
								<article class="gallery-wrap">
									<div class="img-big-wrap">
										<div>
											<a href="#">
											<img src={`./images/items/${product.thumbnail}`} />
											</a>
										</div>
									</div>
									<div class="thumbs-wrap">
										<a href="#" class="item-thumb" >   <img src={`./images/items/${product.thumbnail}`} /></a>
<a href="#" class="item-thumb"> <img src={`./images/items/${product.thumbnail}`} /></a>
										<a href="#" class="item-thumb"> <img src={`./images/items/${product.thumbnail}`} /></a>
										<a href="#" class="item-thumb"> <img src={`./images/items/${product.thumbnail}`} /></a>
									</div></article>
							</div>
						</aside>
						<main class="col-md-6">
							<article class="product-info-aside">
								<h2 class="title mt-3">{product.title} </h2>
								<div class="rating-wrap my-3">
									<ul class="rating-stars">
										<li jstyle={cardTextStyle} class="stars-active">
											<i class="fa fa-star"></i> <i class="fa fa-star"></i>
											<i class="fa fa-star"></i> <i class="fa fa-star"></i>
											<i class="fa fa-star"></i>
										</li>
										<li>
											<i class="fa fa-star"></i> <i class="fa fa-star"></i>
											<i class="fa fa-star"></i> <i class="fa fa-star"></i>
											<i class="fa fa-star"></i>
										</li>
									</ul>
									<small class="label-rating text-muted">132 reviews</small>
									<small class="label-rating text-success">
										<i class="fa fa-clipboard-check"></i> 154 orders{" "}
									</small>
								</div>
								<div class="mb-3">
									<var class="price h4">${product.price}</var>

