import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = async (event)  => {
    const input = event.target.value;
    setSearchQuery(input);

    try {
        if (!input.trim()) {
            setSearchResults([]);
            setDropdownOpen(false);
        } else {
            const productResponse = await axios.get(`http://localhost:8080/api/products`);
            const products = productResponse.data;
            setSearchResults(products.filter((item) =>
                item.title?.toLowerCase().includes(input.toLowerCase())
            ));
            setDropdownOpen(true);
        }
    } catch (error) {
        console.error('Error during search:', error);
    }
    return (

        <section class="section-content padding-y">
		<div class="card mx-auto" style={{ width: '520px' }} >
			<article class="card-body">
				<header class="mb-4"><h4 class="card-title">Sign up</h4></header>
                <form class="form-row">
                <div>
            <label>Fullname:</label>
            <input required="name"
						class="form-control"
							type="fullname"
							placeholder="Fullname"
							value={fullname}
							onChange={(e) => setFullname(e.target.value)}
						/>

            <label>Password:</label>
            <input required="password"
						class="form-control"
							type="password"
							placeholder="Mật khẩu"
							value={password}
							onChange={(e) => setPassword(e.target.value)}
						/>

<button    onClick={handleLogin} type="submit" class="btn btn-primary btn-block"> Login  </button>
<a

                  href="/register"

                >
                 Register
                </a>
        </div>

                </form>

			</article>
		</div>


		</section>

    );
};




export default Login;
